import {createSlice} from '@reduxjs/toolkit';


const cartFunctionality =   createSlice({
    name: 'Add Item',
    initialState : {
        items: [],
        totalQuanity: 0,
        totalAmount: 0,
    },
    reducers:{
        addItemToCart(state,action){
            const item = action.payload;
            state.totalQuanity++;
            const existingItem = state.items.find(c => c.id === item.id)
                if( !existingItem ) {
                        state.items.push(
                            {
                                itemId: item.id,
                                 price: item.price, 
                                 quantity:1, 
                                totalPrice:item.price,
                                name:item.title});
                }
                else{
                    existingItem.quantity++;
                    existingItem.totalPrice = existingItem.totalPrice + existingItem.price;
                }
            },

        removeItemsFromCart(state,action){
            const item = action.payload;
            state.totalQuanity--
            const existingItem = state.items.find(c=> c.id === item.id);
            if(existingItem.quantity === 0){
                state.items.filter(c => c.id !== item.id)
            }else{
                existingItem.quantity--;
                existingItem.totalPrice = existingItem.totalPrice - existingItem.price;

            }
        }
        },
    })
export const itemActions = cartFunctionality.actions;

export default cartFunctionality;
